import React, { useEffect, useState } from 'react';
import './style.css';
import { Link } from 'react-router-dom';
import axios from 'axios';

export function CuerpoLab() {
  const [campos, setCampos] = useState({
    nombre_laboratorio: "",
    bloque : "",
    nivel: "",
    aula: "",
    capacidad: "",
    area: "",
    red_hidraulica: false,
    red_gases_especiales: false,
    red_electrica: false,
    red_aire: false,
    otros: "",
  });

  const postLabs = async e => {
    let lab = {
      nombre_laboratorio: campos.nombre_laboratorio,
      bloque: campos.bloque,
      nivel: campos.nivel,
      aula: campos.aula,
      capacidad: campos.capacidad,
      area: campos.area,
      red_hidraulica: campos.red_hidraulica,
      red_gases_especiales: campos.red_gases_especiales,
      red_electrica: campos.red_electrica,
      red_aire: campos.red_aire,
      otros: campos.otros,
    };
    e.preventDefault();
    await axios.post('http://localhost:5610/api/v1/laboratorios', lab);
  };

  const onChange = e => {
    const value = e.target.value;
    setCampos({
      ...campos,
      [e.target.name]: value,
    });
    console.log(campos.nombre_laboratorio);
  };
  const onChangeCheck = e => {
    const value = e.target.checked;
    setCampos({
      ...campos,
      [e.target.name]: value,
    });
    console.log(e.target.checked);
    
  }

  return (
    <div className="margen" id="fuente">
      <div className="shadow-lg p-3 mb-5 bg-body rounded">
        <h1 calign="center" id="margen-titulo">
          Agregar Labotoratorio
        </h1>

        <nav className="margen">
          <div className="row">
            <div className="col">
              <label>Nombre</label>
              <input
                className="form-control form-control-lg"
                type="text"
                value={campos.nombre_laboratorio}
                onChange={onChange}
              />
            </div>

            <div className="col">
              <label>Bloque</label>
              <input
                className="form-control form-control-lg"
                type="text"
                value={campos.bloque}
                onChange={onChange}
              />
            </div>
          </div>
          <br />
          <div className="row">
            <div className="col">
              <label>Nivel</label>
              <input
                className="form-control form-control-lg"
                type="text"
                value={campos.nivel}
                onChange={onChange}
              />
            </div>

            <div className="col">
              <label>Aula</label>
              <input
                className="form-control form-control-lg"
                type="text"
                value={campos.aula}
                onChange={onChange}
              />
            </div>
          </div>
          <br />
          <div className="row">
            <div className="col">
              <label>Capacidad</label>
              <input
                className="form-control form-control-lg"
                type="number"
                value={campos.capacidad}
                onChange={onChange}
              />
            </div>

            <div className="col">
              <label>Área</label>
              <input
                className="form-control form-control-lg"
                type="number"
                value={campos.area}
                onChange={onChange}
              />
            </div>
          </div>
          <br />
          <h2>Redes</h2>
          <br />
          <div className="row">
            <div className="col" id="espacio-checkbox-text">
              <label htmlFor="">Hidráulica</label>
              <input style={{ marginLeft: '8%' }} type="checkbox" id="hidraulica" value={campos.red_hidraulica}
                onChange={onChangeCheck}/>
            </div>
            <div className="col">
              <label>Gases</label>
              <input style={{ marginLeft: '8%' }} type="checkbox" id="gases" value={campos.red_gases_especiales}
                onChange={onChangeCheck}/>
            </div>
            <div className="col">
              <label>Eléctrica</label>
              <input style={{ marginLeft: '8%' }} type="checkbox" id="electrica" value={campos.red_electrica}
                onChange={onChangeCheck}/>
            </div>
            <div className="col">
              <label>Aire</label>
              <input style={{ marginLeft: '8%' }} type="checkbox" id="aire" value={campos.red_aire}
                onChange={onChangeCheck}/>
            </div>
          </div>
          <br />

          <label>Otros</label>
          <br />
          <textarea name="comentarios" id="comen" rows="3" value={campos.otros}
                onChange={onChange}></textarea>

          <div id="btns">
            <div className="btn-group">
              <Link style={{ textDecoration: 'none', color: '#000' }} to="/LabHome">
                <button id="tamaño-botones" type="button" className="btn btn-outline-danger">
                  Cancelar
                </button>
              </Link>
              <button
                id="tamaño-botones"
                type="button"
                className="btn btn-outline-success"
                style={{ marginLeft: '10%' }}
              >
                Crear
              </button>
            </div>
          </div>
          <br />
        </nav>
      </div>
    </div>
  );
}
