import React, { useState, useEffect } from 'react';
import './style-eH.css';
import { Link } from 'react-router-dom';
import axios from 'axios';
import * as FileSaver from "file-saver";

/*
import {
  DropdownMenu,
  DropdownItem,
  UncontrolledDropdown,
  DropdownToggle,
} from "reactstrap"; */

export function ElementosHome() {
  const [elementos, setElementos] = useState([]);

  const getElementos = async () => {
    await axios
      .get('http://localhost:5610/api/v1/elementos')
      .then(res => setElementos(res.data.data))
      .catch(err => console.log(err));
  };

  useEffect(() => {
    getElementos();
  }, []);

  const exportToExcel = (apiData,fileName)=>{
    const fileType =
    "application/vnd.openxmlformats-officedocument.spreadsheetml.sheet;charset=UTF-8";
    const fileExtension = ".xlsx";
    //const ws = XLSX.utils.json_to_sheet(apiData);
    //const wb = { Sheets: { data: ws }, SheetNames: ["data"] };
    //const excelBuffer = XLSX.write(wb, { bookType: "xlsx", type: "array" });
    //const data = new Blob([excelBuffer], { type: fileType });
    FileSaver.saveAs(elementos, fileName + fileExtension);
    
  }

  return (
    <div>
      <Link style={{ textDecoration: 'none', color: '#000' }} to="/elementos">
        <button id="margen-btn" type="button" className="btn btn-outline-success" style={{marginRight: "14%"}}>
          Agregar Elemento
        </button>
      </Link>

      <button id='margen-btn' type='button' className='btn btn-outline-success' onClick={(e)=>exportToExcel(elementos,'elementos')}>Descargar excel</button>

      <br />
      <nav id="margen">
        <table className="table" id="letra-tabla">
          <thead style={{ color: 'white', backgroundColor: '#aa0013' }}>
            <tr>
              <th scope="col">Código</th>
              <th scope="col">Número inventario</th>
              <th scope="col">Nombre</th>
              <th scope="col">Marca</th>
              <th scope="col">Cantidad</th>
              <th scope="col">Modelo</th>
              <th scope="col">Tipo</th>
              <th scope="col">Serie</th>
              <th scope="col">Fecha actualización</th>
              <th scope="col">Estado</th>
              <th scope="col">Observaciones</th>
              <th scope="col">Descripcion</th>

              <th scope="col">
                <center>
                  <button type="button" className="btn btn-outline-secondary">
                    <img
                      alt="view elements"
                      //src={require('./pencil_white.png')}
                      width="20"
                      height="20"
                    />
                  </button>
                </center>
              </th>
            </tr>
          </thead>
          <tbody>
            {elementos.map(item => {
              return (
                <tr key={item.codigo_elemento}>
                  <th scope="row">{item.codigo_elemento}</th>
                  <td>{item.numero_inventario}</td>
                  <td>{item.nombre_elemento}</td>
                  <td>{item.marca}</td>
                  <td>{item.cantidad}</td>
                  <td>{item.modelo}</td>
                  <td>{item.tipo}</td>
                  <td>{item.serie}</td>
                  <td>{item.fecha_actualizacion}</td>
                  <td>{item.estado}</td>
                  <td>{item.observaciones}</td>
                  <td>{item.descripcion}</td>

                </tr>
              );
            })}

            
            
            
          </tbody>
        </table>
      </nav>
    </div>
  );
}
