import React, {useState,useEffect} from 'react'
import { Link } from 'react-router-dom'
import "./style.css";
import axios from "axios";


export function CrearFicha() {

    const [labs, setLabs] = useState([]);
    const getLabs = async () => {
      await axios
        .get('http://localhost:5610/api/v1/laboratorios')
        .then(res => setLabs(res.data.data))
        .catch(err => console.log(err));
    };

    useEffect(() => {
      getLabs();
    }, []);

    //useState para Fichas
    const [campos, setCampos] = useState({
      codigo_ficha: 0,
      id_laboratorio: '',
      fecha_emision: 0,
      version: '',
    });
    const postElements = async e => {
      let lab = {
        codigo_ficha: campos.codigo_ficha,
        nombre_elemento: campos.nombre_elemento,
        cantidad: campos.cantidad,
        marca: campos.marca,
        modelo: campos.modelo,
        tipo: campos.tipo,
        serie: campos.serie,
        estado: campos.estado,
        observaciones: campos.observaciones,
        descripcion: campos.descripcion,
      };
      e.preventDefault();
      await axios.post('http://localhost:5610/api/v1/elementos', lab);
    };
    return (
      <div>
        <div class="margen" id="fuente">
          
          <div class="shadow-lg p-3 mb-5 bg-body rounded">
            <h1 calign="center" id="margen-titulo">
              Agregar Ficha
            </h1>
  
            <nav class="margen">
              <div class="row">
                <div class="col">
                <br />
              <select className="form-select-lg">
                <option >Seleccione un laboratorio</option>
                {labs.map(item => {
                  return (
                    <option key={item.id_laboratorio}>{item.nombre_laboratorio}</option>
                  );
                })}
              </select>
                </div>
  
                
              </div>
              <br />
              <div class="row">
                <div class="col">
                  <label>Versión</label>
                  <input
                    class="form-control form-control-lg"
                    type="text"
                    id="id_version"
                  />
                </div>
                <div class="col">
                  <label>Fecha emisión</label>
                  <input
                    class="form-control form-control-lg"
                    type="text"
                    id="fecha"
                  />
                </div>
              </div>
            
  
  <Link
          style={{ textDecoration: "none", color: "#000" }}
          to="/home"
        >
          <button
            type="button"    
            id="tamaño-botones2"      
            className="btn btn-outline-danger"
            style={{marginRight: "5%"}}
          >
                    Cancelar
          </button>
        </Link>
                <button
                  type="button"
                  id="tamaño-botones2"
                  className="btn btn-outline-success"                
                  style={{ marginleft: "10%" }}
                >
                  Agregar
                </button>
                
            </nav>
          </div>
        </div>
      </div>
    );
  }
